mapper.select:
SELECT r.id, member_id, tel, patient_gender, patient_birthday, patient_diagnosis_name, location, SUBSTRING_INDEX(road_address, ' ', 2) AS road_address, wage, start_date, end_date, start_time, end_time, expire_at, group_concat(day) AS day
FROM recruitment r
JOIN dayofreservation d ON d.recruitment_id = r.id
JOIN suitemember s ON s.id = r.member_id
WHERE
    expire_at > now() AND status = 'P'
    AND wage BETWEEN

        #{it}

    AND start_time >= #{requestDTO.worktime[0]}
    AND end_time <= #{requestDTO.worktime[1]}

        AND r.id IN (
            SELECT DISTINCT recruitment_id
            FROM dayofreservation
            WHERE NOT EXISTS (
                SELECT 1
                FROM dayofreservation d2
                WHERE d2.recruitment_id = dayofreservation.recruitment_id
                AND

                    d2.day = #{day}

            )
        )


        AND patient_diagnosis_name like CONCAT('%', #{requestDTO.search_input}, '%')


        AND SUBSTRING_INDEX(SUBSTRING_INDEX(road_address, ' ', 2), ' ', -1) in

            #{it}



        AND patient_gender = #{requestDTO.gender[0]}

GROUP BY r.id, member_id, tel, patient_gender, patient_diagnosis_name, location, road_address, wage, start_date, end_date, start_time, end_time, expire_at
;

SELECT id, patient_name, patient_diagnosis_name, start_date, end_date FROM recruitment
WHERE member_id = #{login_id}
AND status = 'P'
AND expire_at >= now();
